export function interes({montoInicial:t,tna:n,plazo:o,ciclos:e,adicional:i}){const r=n/365/100;i*=1;let c=t*(r*o+1);c+=i;if(e>1){return interes({montoInicial:c,tna:n,plazo:o,ciclos:e-1,adicional:i})}return c}export function teayt(t,n,o){const e=100*(Math.pow(1+t/100/12,12)-1);const i=100*(Math.pow(1+t/100/(365/n),o)-1);return{tea:e,tet:i}}export function rendimiento(t,n){const o=t/100;const e=n/100;return((1+e)/(1+o)-1)*100}export function numAjustado(t=0,n){return n/(1+t/100)}export function render(t,n){t.value=n}