export function dotFormat(e,t=false){const r=t?e.replace(/\./g,""):e.replace(/\./,",");const n=/\B(?=(\d{3})+(?!\d))/g;const o=r.replace(n,".");return o}export function textToNumObject(e){function t(e){let t=e.replace(/\./g,"");t=t.replace(",",".");const r=Number.parseFloat(t);return r}for(const r in e){e[r]=t(e[r]);if(isNaN(e[r])){e[r]=0}}return e}